import React, {Component} from 'react';
import Navigation from './Navigation';
import Heading from './Heading';
import Submit from './Submit';
import Bottom from './Bottom';


class App  extends Component {
    constructor(){
        super();
        this.state={
            link:'',
            shrinks:[],
            error:'none',
            border:''
        };
    }

    linkInput = (event) => {
        const {value} = event.target;
        this.setState({link: value})
    }

    submitClick = () => {
        if(this.state.link.length === 0){
            this.setState({error:'initial',border:'hsl(0, 87%, 67%)'})
        }else{
            this.setState({error:'none',border:''})
        }
    }




    render(){
        return(
            <div>
                <Navigation/>
                <Heading/>
                <Submit typing={this.linkInput} submit={this.submitClick} color={this.state.border} errorDisplay={this.state.error}/>
                <Bottom/>
            </div>
        )
    }
}



export default App;
